static int yy_init_globals (void)
{
        /* Initialization is the same as for the non-reentrant scanner.
     * This function is called from puffer_lex_destroy(), so don't allocate here.
     */

    /* We do not touch puffer_lineno unless the option is enabled. */
    puffer_lineno =  1;
    
    (yy_buffer_stack) = 0;
    (yy_buffer_stack_top) = 0;
    (yy_buffer_stack_max) = 0;
    (yy_c_buf_p) = (char *) 0;
    (yy_init) = 0;
    (yy_start) = 0;

/* Defined in main.c */
#ifdef YY_STDINIT
    puffer_in = stdin;
    puffer_out = stdout;
#else
    puffer_in = (FILE *) 0;
    puffer_out = (FILE *) 0;
#endif

    /* For future reference: Set errno on error, since we are called by
     * puffer_lex_init()
     */
    return 0;
}

/* puffer_lex_destroy is for both reentrant and non-reentrant scanners. */
int puffer_lex_destroy  (void)
{
    
    /* Pop the buffer stack, destroying each element. */
	while(YY_CURRENT_BUFFER){
		puffer__delete_buffer(YY_CURRENT_BUFFER  );
		YY_CURRENT_BUFFER_LVALUE = NULL;
		puffer_pop_buffer_state();
	}

	/* Destroy the stack itself. */
	puffer_free((yy_buffer_stack) );
	(yy_buffer_stack) = NULL;

    /* Reset the globals. This is important in a non-reentrant scanner so the next time
     * puffer_lex() is called, initialization will occur. */
    yy_init_globals( );

    return 0;
}

